import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {
  isScrolled = false;
  logoImage = '../../assets/img/logo_full.svg';

  @HostListener('window:scroll')
  onScroll() {
    this.isScrolled = (window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop) > 0;
    this.logoImage = this.isScrolled ? '../../assets/img/logo_full.svg' : '../../assets/img/logo_full_alt.svg';
  }
}
